import { Component } from "@angular/core";

@Component({
    selector: "app-header-dettaglio-partita",
    templateUrl: "./header-dettaglio-partita.component.html",
    styleUrls: ["./header-dettaglio-partita.component.scss"],
})
export class HeaderDettaglioPartitaComponent {
    a = {
        stadium: "Little Caesars Arena",
        city: "Detroit",
        squadre: [
            {
                nome: "Trail Brazers",
                logo: "/assets/s-l1200.webp",
            },
            {
                nome: "Trail Brazers",
                logo: "/assets/s-l1200.webp",
            },
        ],
        date: "2024-12-22T20:30:00.000Z",
    };
}
<div class="mt-10 p-4 text-xl font-inter text-center bg-bianco color-nero">
    {{a.stadium}}, {{a.city}}
</div>
<div class="flex justify-center items-center bg-white/5 w-full min-h-[10vh] border-y">
    <div class="mr-24 text-[4rem]">
        <img src="{{a.squadre[0].logo}}" alt="Logo {{a.squadre[0].nome}}">
        {{a.squadre[0].nome.toUpperCase()}}
    </div>
    <p class="font-inter font-extralight text-3xl">vs</p>
    <div class="ml-24 text-[4rem]">
        <img src="{{a.squadre[0].logo}}" alt="Logo {{a.squadre[0].nome}}">
        {{a.squadre[0].nome.toUpperCase()}}
    </div>
</div>
