import { Component, OnInit } from "@angular/core";
import dayjs from "dayjs";
import localizedFormat from "dayjs/plugin/localizedFormat"; // Importa il plugin per il formato localizzato
import "dayjs/locale/it"; // Importa la lingua italiana

@Component({
    selector: "app-quarters-dettaglio-partita",
    templateUrl: "./quarters-dettaglio-partita.component.html",
    styleUrls: ["./quarters-dettaglio-partita.component.scss"],
})
export class QuartersDettaglioPartitaComponent implements OnInit {
    a = {
        squadre: [
            {
                nome: "STK",
                logo: "/assets/s-l1200.webp",
                Q1: 120,
                Q2: 130,
                Q3: 135,
                Q4: 160,
            },
            {
                nome: "CIA",
                logo: "/assets/s-l1200.webp",
                Q1: 100,
                Q2: 120,
                Q3: 125,
                Q4: 200,
            },
        ],
    };

    date = "";
    hour = "";

    ngOnInit() {
        this.convertDate();
        this.convertHour();
    }

    convertDate() {
        const dateDayJs = dayjs(this.a.date).locale("it");
        this.date = dateDayJs.format("ddd D MMM, YYYY");
    }

    convertHour() {
        const hourDayJs = dayjs(this.a.date).locale("it");
        this.hour = hourDayJs.format("HH:mm");
    }
}
