import { Component, OnInit } from "@angular/core";
import dayjs from "dayjs";

@Component({
    selector: "app-header-dettaglio-partita",
    templateUrl: "./header-dettaglio-partita.component.html",
    styleUrls: ["./header-dettaglio-partita.component.scss"],
})
export class HeaderDettaglioPartitaComponent implements OnInit {
    a = {
        stadium: "Little Caesars Arena",
        city: "Detroit",
        squadre: [
            {
                nome: "Trail Brazers",
                logo: "/assets/s-l1200.webp",
            },
            {
                nome: "Raptors",
                logo: "/assets/s-l1200.webp",
            },
        ],
        date: "2024-12-22T20:30:00.000Z",
    };

    ngOnInit() {
        this.convertDate();
    }

    convertDate() {
        const start = dayjs();
        console.log(start);
    }
}
<div class="mt-10 p-2 text-l font-inter text-center bg-bianco color-nero">
    {{a.stadium}}, {{a.city}}
</div>
<div class="flex justify-center items-center w-full min-h-[10vh] p-4 border-y bg-white/5">
    <div class="mr-24">
        <img src="{{a.squadre[0].logo}}" class="inline-block w-[4rem] mr-10" alt="Logo {{a.squadre[0].nome}}">
        <p class="inline-block align-middle text-[4rem]">{{a.squadre[0].nome.toUpperCase()}}</p>
    </div>
    <p class="font-inter font-extralight text-3xl">vs</p>
    <div class="ml-24">
        <p class="inline-block align-middle text-[4rem]">{{a.squadre[1].nome.toUpperCase()}}</p>
        <img src="{{a.squadre[1].logo}}" class="inline-block w-[4rem] ml-10" alt="Logo {{a.squadre[1].nome}}">
    </div>
</div>
<div class="flex justify-center items-center w-full min-h-[10vh] p-4 border-y bg-white/5">

</div>
